import React, { useState } from 'react';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [accessToken, setAccessToken] = useState('');
  const [portalUrl, setPortalUrl] = useState('');
  const [showWebView, setShowWebView] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState('');

  const handleLogin = async () => {
    // Step 1: Validate email and password
    if (!email || !password) {
      setError('Please enter email and password.');
      return;
    }
    setError('');

    try {
      const response = await fetch('https://safeguard-me-coding-exercise.azurewebsites.net/api/Login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      setAccessToken(data.accessToken);

      // Step 2: Get Portal URL
      const portalResponse = await fetch('https://safeguard-me-coding-exercise.azurewebsites.net/api/PortalUrl', {
        headers: {
          Authorization: `Bearer ${data.accessToken}`,
        },
      });
      const portalData = await portalResponse.text();
      setPortalUrl(portalData);
      setShowWebView(true);
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred. Please try again later.');
    }
  };

  const handleWebViewNavigation = (event) => {
    // Step 4: Detect successful upload
    if (event.url === 'https://safeguard-me-coding-exercise.azurewebsites.net/api/PortalSuccess') {
      setSuccess(true);
      setShowWebView(false);
    }
  };

  return (
    <div>
      <h1>CV Uploader</h1>
      {/* Display error message */}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {!accessToken ? (
        <div>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleLogin}>Login</button>
        </div>
      ) : null}
      {showWebView && (
        <iframe
          src={portalUrl}
          style={{ width: '100%', height: '400px', border: 'none' }}
          onLoad={() => console.log('WebView loaded')}
          onError={(e) => console.error('WebView error:', e)}
          onNavigationStateChange={handleWebViewNavigation}
        />
      )}
      {success && <p>Document uploaded successfully!</p>}
    </div>
  );
}

export default App;
