import React, { useState } from 'react';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [accessToken, setAccessToken] = useState('');
  const [portalUrl, setPortalUrl] = useState('');
  const [showWebView, setShowWebView] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState('');

  const validateEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  };

  const validatePassword = (password) => {
    return password.length >= 6; // Minimum 6 characters
  };

  const handleLogin = async () => {
    if (!email || !password) {
      setError('Please enter both email and password.');
      return;
    }

    if (!validateEmail(email)) {
      setError('Invalid email address.');
      return;
    }

    if (!validatePassword(password)) {
      setError('Password must be at least 6 characters long.');
      return;
    }

    try {
      const response = await fetch('https://safeguard-me-coding-exercise.azurewebsites.net/api/Login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        setError('Invalid email or password.');
        return;
      }

      const data = await response.json();
      setAccessToken(data.accessToken);

      // Step 2: Get Portal URL
      const portalResponse = await fetch('https://safeguard-me-coding-exercise.azurewebsites.net/api/PortalUrl', {
        headers: {
          Authorization: `Bearer ${data.accessToken}`,
        },
      });
      const portalData = await portalResponse.text();
      setPortalUrl(portalData);
      setShowWebView(true);
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred. Please try again later.');
    }
  };

  const handleWebViewNavigation = (event) => {
    // Step 4: Detect successful upload
    if (event.url === 'https://safeguard-me-coding-exercise.azurewebsites.net/api/PortalSuccess') {
      setSuccess(true);
      setShowWebView(false);
    }
  };

  return (
    <div className="container">
      <h1 className="title">CV Uploader</h1>
      <div className="form-container">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="input"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="input"
        />
        <button onClick={handleLogin} className="button">Login</button>
        {error && <p className="error">{error}</p>}
      </div>
      {showWebView && (
        <iframe
          src={portalUrl}
          style={{ width: '100%', height: '400px', border: 'none' }}
          onLoad={() => console.log('WebView loaded')}
          onError={(e) => console.error('WebView error:', e)}
          onNavigationStateChange={handleWebViewNavigation}
        />
      )}
      {success && <p className="success">Document uploaded successfully!</p>}
    </div>
  );
}

export default App;
